import React from 'react';
import { menuItems } from '../data/menuData';

const MenuCard = () => {
  const handleImageError = (e) => {
    e.target.src = '/images/menu/placeholder.jpg';
  };

  const renderSpicyLevel = (level) => {
    return Array(level).fill('ðŸŒ¶ï¸').join('');
  };

  const renderProteinTag = (protein) => {
    if (!protein || protein === 'none') return null;

    if (Array.isArray(protein)) {
      return protein.map((p, index) => (
        <span 
          key={index} 
          className="px-2 py-1 bg-orange-50 text-orange-700 rounded-full text-sm mr-2 border border-orange-200"
        >
          {p}
        </span>
      ));
    }

    return (
      <span className="px-2 py-1 bg-orange-50 text-orange-700 rounded-full text-sm mr-2 border border-orange-200">
        {protein}
      </span>
    );
  };

  const renderCategoryTags = (categories) => {
    return categories.map((category, index) => (
      <span 
        key={index} 
        className="px-2 py-1 bg-blue-50 text-blue-700 rounded-full text-sm mr-2 border border-blue-200"
      >
        {category}
      </span>
    ));
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 to-orange-50 py-8 px-4">
      <div className="max-w-5xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-800 mb-2 font-serif">
            Our Family Menu
          </h1>
          <p className="text-gray-600">
            Delicious homestyle dishes from our kitchen to your table
          </p>
        </div>

        {/* Menu Cards Grid */}
        <div className="grid grid-cols-1 gap-8">
          {menuItems.map((item) => (
            <div 
              key={item.id}
              className="bg-white rounded-2xl shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300 border border-gray-100"
            >
              <div className="flex flex-col md:flex-row">
                {/* Image Container */}
                <div className="md:w-1/3 h-64 md:h-auto relative overflow-hidden">
                  <img
                    src={item.imageUrl}
                    alt={item.romanizedName}
                    className="w-full h-full object-cover transform hover:scale-105 transition-transform duration-300"
                    onError={handleImageError}
                  />
                </div>

                {/* Content Container */}
                <div className="flex-1 p-6">
                  {/* Title Section */}
                  <div className="mb-4">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-1">
                      {item.koreanName} 
                      <span className="text-gray-600 font-normal">
                        ({item.romanizedName})
                      </span>
                    </h2>
                    <p className="text-gray-600 leading-relaxed">
                      {item.description}
                    </p>
                  </div>

                  {/* Tags Container */}
                  <div className="flex flex-wrap gap-2 mt-4">
                    {/* Protein Tags */}
                    {renderProteinTag(item.protein)}

                    {/* Spicy Level */}
                    {item.spicyLevel > 0 && (
                      <span className="px-2 py-1 bg-red-50 text-red-700 rounded-full text-sm mr-2 border border-red-200">
                        {renderSpicyLevel(item.spicyLevel)}
                      </span>
                    )}

                    {/* Category Tags */}
                    {renderCategoryTags(item.categories)}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MenuCard;
