import { useEffect } from 'react';
import { jsPDF } from 'jspdf';
import html2canvas from 'html2canvas';

function saveMenuAsPDF() {
  // Capture the menu area
  const element = document.getElementById('menu'); // Update with the correct ID or class of your menu container
  html2canvas(element, {
    backgroundColor: '#000',
    scale: 2, // Increase scale for better resolution
    useCORS: true // Enables cross-origin for images if needed
  }).then(canvas => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');

    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (canvas.height * pdfWidth) / canvas.width;

    pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
    pdf.save('Choi_Family_Menu.pdf');
  });
}


export default function App() {
    useEffect(() => {
        saveMenuAsPDF();
    }, []); // This will generate the PDF when the component mounts

    return (
        <div>
            {/* Your existing layout */}
            <div id="menu" className="menu-section">
                {/* Menu content goes here */}
            </div>
        </div>
    );
}
