import React from 'react';
import menuItems from '../data/menuData';
import styles from './MenuCard.module.css';

const MenuCard = () => {
  // Helper function to render protein tags with specific protein icons
  const renderProteinTag = (protein) => {
    if (!protein || protein === 'none') return null;

    if (Array.isArray(protein)) {
      return protein.map((p, index) => (
        <span key={index} className={`${styles.tag} ${styles.proteinTag}`}>
          <img 
            src={`/images/tag/${p.toLowerCase()}.png`}
            alt={p}
            className={styles.tagIcon}
          />
          {p}
        </span>
      ));
    }

    return (
      <span className={`${styles.tag} ${styles.proteinTag}`}>
        <img 
          src={`/images/tag/${protein.toLowerCase()}.png`}
          alt={protein}
          className={styles.tagIcon}
        />
        {protein}
      </span>
    );
  };

  // Helper function to render spicy level with repeated spicy icons
  const renderSpicyLevel = (level) => {
    if (level <= 0) return null;

    return (
      <span className={`${styles.tag} ${styles.spicyTag}`}>
        {[...Array(level)].map((_, index) => (
          <img 
            key={index}
            src="/images/tag/spicy.png"
            alt="spicy"
            className={styles.tagIcon}
          />
        ))}
      </span>
    );
  };

  // Helper function to render category tags
  const renderCategoryTag = (category) => (
    <span className={`${styles.tag} ${styles.categoryTag}`}>
      <img 
        src={`/images/tag/${category.toLowerCase()}.png`}
        alt={category}
        className={styles.tagIcon}
      />
      {category}
    </span>
  );

  return (
    <div className="min-h-screen bg-slate-50 py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header Section */}
        <header className="text-center mb-12">
          <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4 font-serif">
            Family Restaurant Menu
          </h1>
          <div className="w-24 h-1 bg-amber-400 mx-auto mb-4"></div>
        </header>

        {/* Menu Items */}
        <div className="space-y-6">
          {menuItems.map((item) => (
            <div key={item.id} className={styles.menuCard}>
              <div className="md:flex">
                {/* Image Container */}
                <div className={styles.imageContainer}>
                  <img
                    src={item.imageUrl}
                    alt={item.romanizedName}
                    className={styles.menuImage}
                    onError={(e) => {
                      e.target.src = '/images/menu/placeholder.jpg';
                    }}
                  />
                </div>

                {/* Content Container */}
                <div className={styles.contentContainer}>
                  {/* Title */}
                  <div className="mb-4">
                    <h2 className={styles.title}>
                      <span className="font-semibold">{item.koreanName}</span>
                      <span className="text-gray-600 ml-2">({item.romanizedName})</span>
                    </h2>
                    <p className={styles.description}>{item.description}</p>
                  </div>

                  {/* Tags */}
                  <div className={styles.tagContainer}>
                    {/* Protein Tags */}
                    {renderProteinTag(item.protein)}

                    {/* Spicy Level */}
                    {renderSpicyLevel(item.spicyLevel)}

                    {/* Categories */}
                    {item.categories.map((category, index) => (
                      <React.Fragment key={index}>
                        {renderCategoryTag(category)}
                      </React.Fragment>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MenuCard;
