import React from 'react';

// Since we can't import external CSS modules or images, we'll use inline styles
// and include the menu data directly in the component
const MenuCard = () => {
  // Styles
  const styles = {
    menuContainer: {
      minHeight: '100vh',
      padding: '2rem',
      color: 'white',
      backgroundColor: '#111827', // Dark background as fallback
    },
    menuHeader: {
      textAlign: 'center',
      marginBottom: '4rem',
      padding: '0 1rem'
    },
    mainTitle: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
      color: 'white'
    },
    subTitle: {
      fontSize: '1.25rem',
      color: '#FCD34D',
      fontWeight: '300',
      marginBottom: '3rem'
    },
    menuContent: {
      maxWidth: '72rem',
      margin: '0 auto',
      padding: '0 1rem'
    },
    categorySection: {
      marginBottom: '4rem'
    },
    categoryTitle: {
      display: 'inline-block',
      padding: '0.5rem 1.5rem',
      backgroundColor: '#FCD34D',
      color: 'black',
      fontSize: '1.5rem',
      fontWeight: 'bold',
      borderRadius: '0.5rem',
      marginBottom: '2rem'
    },
    subCategoryTitle: {
      fontSize: '1.5rem',
      color: '#FCD34D',
      marginBottom: '1.5rem',
      marginLeft: '1rem',
      fontWeight: 'bold'
    },
    menuCard: {
      display: 'flex',
      backgroundColor: 'rgba(17, 24, 39, 0.8)',
      borderRadius: '0.5rem',
      overflow: 'hidden',
      marginBottom: '1.5rem',
      border: '1px solid #FCD34D'
    },
    imageContainer: {
      width: '12rem',
      height: '12rem',
      flexShrink: 0,
      margin: '1rem',
      backgroundColor: 'white',
      borderRadius: '0.5rem',
      overflow: 'hidden'
    },
    menuImage: {
      width: '100%',
      height: '100%',
      objectFit: 'cover'
    },
    contentContainer: {
      flex: 1,
      padding: '1rem',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center'
    },
    itemTitle: {
      fontSize: '1.25rem',
      marginBottom: '0.5rem'
    },
    romanized: {
      color: '#9CA3AF',
      marginLeft: '0.5rem',
      fontWeight: 'normal'
    },
    itemDescription: {
      color: '#D1D5DB',
      marginBottom: '1rem',
      lineHeight: '1.5'
    },
    tagContainer: {
      display: 'flex',
      flexWrap: 'wrap',
      gap: '0.5rem'
    },
    tag: {
      width: '2rem',
      height: '2rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      borderRadius: '9999px',
      backgroundColor: 'rgba(255, 255, 255, 0.9)'
    },
    tagIcon: {
      width: '1.5rem',
      height: '1.5rem',
      objectFit: 'contain'
    }
  };

  // We can use placeholder image from the supported API
  const defaultImage = "/api/placeholder/192/192";

  const handleImageError = (e) => {
    e.target.src = defaultImage;
    e.target.onerror = null;
  };

  const renderProteinTag = (protein) => {
    if (!protein || protein === 'none') return null;

    if (Array.isArray(protein)) {
      return protein.map((p, index) => (
        <span key={index} style={styles.tag}>
          <img 
            src="/api/placeholder/24/24"
            alt={p}
            style={styles.tagIcon}
            onError={(e) => e.target.style.display = 'none'}
          />
        </span>
      ));
    }

    return (
      <span style={styles.tag}>
        <img 
          src="/api/placeholder/24/24"
          alt={protein}
          style={styles.tagIcon}
          onError={(e) => e.target.style.display = 'none'}
        />
      </span>
    );
  };

  return (
    <div style={styles.menuContainer}>
      <header style={styles.menuHeader}>
        <h1 style={styles.mainTitle}>Choi Family Home Dining</h1>
        <h2 style={styles.subTitle}>
          Our family food menu is crafted with generations of cherished recipes
        </h2>
      </header>

      <div style={styles.menuContent}>
        {/* Example rendering of a single menu item */}
        {menuCategories.map((item) => (
          <div key={item.id} style={styles.menuCard}>
            <div style={styles.imageContainer}>
              <img
                src={defaultImage}
                alt={item.romanizedName}
                style={styles.menuImage}
                onError={handleImageError}
              />
            </div>

            <div style={styles.contentContainer}>
              <h4 style={styles.itemTitle}>
                <span>{item.koreanName}</span>
                <span style={styles.romanized}>({item.romanizedName})</span>
              </h4>
              <p style={styles.itemDescription}>{item.description}</p>
              <div style={styles.tagContainer}>
                {renderProteinTag(item.protein)}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenuCard;
