import React from 'react';
import menuItems from '../data/menuData';

const MenuCard = () => {
  const handleImageError = (e) => {
    e.target.src = '/images/menu/placeholder.jpg';
  };

  const renderSpicyLevel = (level) => {
    return Array(level).fill('🌶️').join('');
  };

  const renderProteinTag = (protein) => {
    if (!protein || protein === 'none') return null;

    if (Array.isArray(protein)) {
      return protein.map((p, index) => (
        <span 
          key={index}
          className="px-2 py-1 bg-orange-50 text-orange-700 rounded-full text-sm mr-2 border border-orange-200"
        >
          {p}
        </span>
      ));
    }

    return (
      <span className="px-2 py-1 bg-orange-50 text-orange-700 rounded-full text-sm mr-2 border border-orange-200">
        {protein}
      </span>
    );
  };

  const renderCategoryTags = (categories) => {
    return categories.map((category, index) => (
      <span 
        key={index}
        className="px-2 py-1 bg-blue-50 text-blue-700 rounded-full text-sm mr-2 border border-blue-200"
      >
        {category}
      </span>
    ));
  };

  return (
    <div className="min-h-screen bg-slate-50 py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header Section */}
        <header className="text-center mb-12">
          <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4 font-serif">
            Family Restaurant Menu
          </h1>
          <div className="w-24 h-1 bg-amber-400 mx-auto mb-4"></div>
        </header>

        {/* Menu Items */}
        <div className="space-y-6">
          {menuItems.map((item) => (
            <div 
              key={item.id} 
              className="bg-white rounded-lg shadow-md overflow-hidden transition-all hover:shadow-lg border border-gray-100"
            >
              <div className="md:flex">
                {/* Image Container */}
                <div className="md:w-48 h-48 md:h-auto relative">
                  <img
                    src={item.imageUrl}
                    alt={item.romanizedName}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.target.src = '/images/menu/placeholder.jpg';
                    }}
                  />
                </div>

                {/* Content Container */}
                <div className="p-6 flex-1">
                  {/* Title */}
                  <div className="mb-4">
                    <h2 className="text-xl font-medium text-gray-900">
                      <span className="font-semibold">{item.koreanName}</span>
                      <span className="text-gray-600 ml-2">({item.romanizedName})</span>
                    </h2>
                    <p className="text-gray-600 mt-2 leading-relaxed">
                      {item.description}
                    </p>
                  </div>

                  {/* Tags */}
                  <div className="flex flex-wrap gap-2">
                    {/* Protein Tag */}
                    {item.protein && item.protein !== 'none' && (
                      <span className="px-3 py-1 bg-amber-50 text-amber-700 rounded-full text-sm border border-amber-200">
                        {item.protein}
                      </span>
                    )}

                    {/* Spicy Level */}
                    {item.spicyLevel > 0 && (
                      <span className="px-3 py-1 bg-red-50 text-red-700 rounded-full text-sm border border-red-200">
                        {'🌶️'.repeat(item.spicyLevel)}
                      </span>
                    )}

                    {/* Categories */}
                    {item.categories.map((category, index) => (
                      <span 
                        key={index}
                        className="px-3 py-1 bg-blue-50 text-blue-700 rounded-full text-sm border border-blue-200"
                      >
                        {category}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MenuCard;
