import React from 'react';
// Import menuItems from the data file
import { menuItems } from '../data/menuData';

const MenuCard = () => {
  // Function to handle image loading errors
  const handleImageError = (e) => {
    e.target.src = '/images/menu/placeholder.jpg';
  };

  // Function to render spicy level indicators
  const renderSpicyLevel = (level) => {
    return Array(level).fill('🌶️').join('');
  };

  // Function to render protein tags
  const renderProteinTag = (protein) => {
    if (!protein || protein === 'none') return null;

    if (Array.isArray(protein)) {
      return protein.map((p, index) => (
        <span key={index} className="px-2 py-1 bg-gray-100 text-gray-800 rounded-full text-sm mr-2">
          {p}
        </span>
      ));
    }

    return (
      <span className="px-2 py-1 bg-gray-100 text-gray-800 rounded-full text-sm mr-2">
        {protein}
      </span>
    );
  };

  // Function to render category tags
  const renderCategoryTags = (categories) => {
    return categories.map((category, index) => (
      <span 
        key={index} 
        className="px-2 py-1 bg-blue-100 text-blue-800 rounded-full text-sm mr-2"
      >
        {category}
      </span>
    ));
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="grid grid-cols-1 gap-6">
        {menuItems.map((item) => (
          <div 
            key={item.id}
            className="flex gap-6 bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-shadow"
          >
            {/* Image Container */}
            <div className="w-48 h-48 shrink-0">
              <img
                src={item.imageUrl}
                alt={item.romanizedName}
                className="w-full h-full object-cover rounded-lg"
                onError={handleImageError}
              />
            </div>

            {/* Content Container */}
            <div className="flex flex-col flex-grow">
              {/* Title Section */}
              <div className="mb-2">
                <h2 className="text-xl font-medium text-gray-900">
                  {item.koreanName} ({item.romanizedName})
                </h2>
                <h3 className="text-lg text-gray-700">
                  {item.description}
                </h3>
              </div>

              {/* Tags Container */}
              <div className="mt-auto flex flex-wrap gap-2">
                {/* Protein Tags */}
                {renderProteinTag(item.protein)}

                {/* Spicy Level */}
                {item.spicyLevel > 0 && (
                  <span className="px-2 py-1 bg-red-100 text-red-800 rounded-full text-sm mr-2">
                    {renderSpicyLevel(item.spicyLevel)}
                  </span>
                )}

                {/* Category Tags */}
                {renderCategoryTags(item.categories)}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenuCard;
